import React, {useState} from 'react'
import socket from "../../Utils/socket"
import "./ChatArea.css"

export default function ChatArea({ecdh}) {

    const [encrypted, setEncrypted] = useState(false)
    const [messages, setMessages] = useState([])
    // const [encryptedMessages, setEncyptedMessages] = useState([])

    const toogleEncrypted = ()=>{setEncrypted(!encrypted)}

    let messageContent = "";
    let ref;

    const getContent = (e)=>{
        messageContent =  e.target.value
        ref = e
    }

    const onMessage = (e, content)=>{
        e.prventDefalult()
        ref.target.value = ""
        socket.emit("MESSAGE", content)
        setMessages((Messages) => [
            ...Messages,
            {content, fromSelf: true },
            ]);
        // setEncyptedMessages(...encryptedMessages, {})
    }

    // (encrypted)=>{
    //     let MESSAGES = (encrypted===true)?encryptedMessages:messages; 
    //     return 
    const showMessages = messages.map((message, index)=>{
            if(message.formSelf===true){
                return (
                    <div
                      key={index}
                      className="chat__message sender_message"
                    >
                      {message.content}
                      <span className="chat__time">{new Date().toUTCString()}</span>
                    </div>
                  );
            }
            if(message.formSelf===false){
                return (
                    <div
                      key={index}
                      className="chat__message receiver_message"
                    >
                      {message.content}
                      <span className="chat__time">{new Date().toUTCString()}</span>
                    </div>
                  ); 
            }
            else{return null}
        }
        )
    // }

    socket.on("MESSAGE", (content)=>{
        setMessages(...messages, {content, formSelf:false})
    })


    return (
        <div className="chat_area">
            <div id="chat_navbar">
                <button id="change_text" onClick={toogleEncrypted}>Change Text</button>
                <button id="chat_leave_chat">Leave Chat</button>
            </div>
            <div id="message_box">
                {showMessages}
            </div>
            <form id="form" onSubmit={(e)=>{onMessage(e,messageContent)}}>
                <input id="message" type="text" placeholder="Enter Message" onChange={(e) => getContent(e)}/>
                <button id="submit" type="submit">Send</button>
            </form>
        </div>
    )
}
