import ChatArea from "./Components/ChatArea/ChatArea";
import InfoArea from "./Components/InfoArea/InfoArea";
import UtilsArea from "./Components/UtilsArea/UtilsArea";
import JoinRoom from "./Components/JoinRoom/JoinRoom";
import React, {useRef, useEffect, useState} from 'react';
import socket from "./Utils/socket";
import "./CSS/App.css";

const { ECDH } = require("./Utils/ECDH");

function App() {

  const ecdh = useRef(new ECDH());

  const [secretKey, setSecretKey] =  useState("")
  const [friendPublicKey, setFriendPublicKey] =  useState("")
  const [friendOnline, setFriendOnline] = useState(false)

  const [status, setStatus] = useState([])

  const addStatus = (content)=>{
      setStatus((STATUS)=>[
          ...STATUS, content
      ]) 
  }

  socket.on("NEW_CONNECTION", ()=>{
    console.log("NEW CONNECTION")
    socket.emit("PUBLIC_KEY", ecdh.current.getPublicKey())
    addStatus(`Sent your public Key`)
    addStatus(`NEW USER JOINED`)
  })

  socket.on("ROOM_JOINED", (room)=>{
    socket.emit("PUBLIC_KEY", ecdh.current.getPublicKey())
    addStatus(`SUCCESSFULLY JOINED : ${room}`)
    // addStatus(`Sent your public Key`)
  })

  // setTimeout(()=>{
  //   socket.emit("PUBLIC_KEY", ecdh.current.getPublicKey())
  //   console.log("Sending..........")
  // }, 100)

  socket.on("PUBLIC_KEY", (publicKey)=>{
    setFriendPublicKey(publicKey)
    setSecretKey(ecdh.current.getSecretKey(publicKey))
    setFriendOnline(true)
    console.log(`Secret ${secretKey} Public ${friendPublicKey}`)
    addStatus(`Public Key Received : ${publicKey}`)
  })

  socket.on("USER_DISCONNECTED", ()=>{
    setFriendPublicKey("")
    setSecretKey("")
    setFriendOnline(false)
    addStatus(`USER DISCONNECTED`)
  })

  socket.on("ROOM_FULL", (room)=>{
      addStatus(`${room} is FULL!! Please Try another Room`)
  })

  socket.on('INTRUSION_ATTEMPT', (room)=>{
      addStatus(`Intruder tried to join`)
  })

  // On Mounting
  useEffect(()=>{

  }, [])

//   socket.on('PUBLIC_KEY', (room)=>{
//     addStatus(`NEW USER JOINED`)
// })

  const joinRoom  = (roomName)=>{
    // console.log("Room Name :" + roomName)
    socket.emit("JOIN", roomName)
  }

  return (
    <div className="container">
      <div className="left">
        <ChatArea ecdh={ecdh.current} friendOnline={friendOnline}/>
      </div>  
      <div className="right">
        <JoinRoom joinRoom={(event) => joinRoom(event)}/>
        <InfoArea status={status}/>
        <UtilsArea ecdh={ecdh.current} secretKey={secretKey} friendPublicKey={friendPublicKey}/>
      </div>
    </div>
  );
}

export default App;
