const crypo = require("crypto")

class ECDH{

    constructor(){
        this.algorithm = 'aes-256-ctr';
        this.alice = crypo.createECDH("secp256k1")
        this.alice.generateKeys()
    }

    getPublicKey(){
        return this.alice.getPublicKey().toString('base64')
    }

    getPrivateKey(){
        return this.alice.getPrivateKey().toString('base64')
    }

    getSecretKey(bob_publicKey){
        return this.alice.computeSecret(bob_publicKey, 'base64', 'hex')
    }



}


module.exports = {ECDH};
