import React, {useState} from 'react'
import socket from '../../Utils/socket'
import "./InfoArea.css"

export default function InfoArea() {

    const [status, setStatus] = useState([])

    const showStatus = status.map((Status, index)=>{
        return <p key={index}>{Status}</p>
    })

    const scrollDown = ()=>{
        document.getElementById("message_box").scrollTop = document.getElementById("message_box").scrollHeight;
    }

    const addStatus = (content)=>{
        setStatus((STATUS)=>[
            ...STATUS, content
        ]) 
        scrollDown()
    }

    socket.on("ROOM FULL", (room)=>{
        addStatus(`${room} is FULL!! Please Try another Room`)
    })

    socket.on('INTRUSION_ATTEMPT', (room)=>{
        addStatus(`Intruder tried to join`)
    })

    socket.on('USER_DISCONNECTED', (room)=>{
        addStatus(`USER DISCONNECTED`)
    })

    socket.on('ROOM_JOINED', (room)=>{
        addStatus(`SUCCESSFULLY JOINED : ${room}`)
    })

    socket.on('ROOM_JOINED', (room)=>{
        addStatus(`SUCCESSFULLY JOINED : ${room}`)
    })

    socket.on('NEW_CONNECTION', (room)=>{
        addStatus(`NEW USER JOINED`)
    })

    // Public Key Received Status

    return (
        <div  className="info_area">
            <div id="info_navbar">
                <button id="info_header">Status Info</button>
            </div>
            <div id="info">
                {showStatus}
            </div>
        </div>
    )
}
