import React from 'react'
import "./ChatArea.css"

export default function ChatArea() {

    // const scroll_down = ()=>{
    //     const objDiv = document.getElementById("message_box")
    //     objDiv.scrollTop = objDiv.scrollHeight;
    // }


    return (
        <div className="chat_area">
            <div id="message_box">


                <div
                //   key={index}
                className="chat__message receiver_message"
                >
                <span className="chat__name">Name</span><br/>  
                Message
                <span className="chat__time">{new Date().toUTCString()}</span>
                </div>
                <div
                // key={index}
                className="chat__message sender_message"
                >
                <span className="chat__name">Name</span><br/>  
                Message
                <span className="chat__time">{new Date().toUTCString()}</span>
                </div>


            </div>
            <form id="form">
                <input type="text" placeholder="Enter Message"/>
                <button id="submit" type="submit">Send</button>
                {/* <button id="scroll_down" onClick={scroll_down}>Down</button> */}
            </form>
        </div>
    )
}
